$(document).ready(function(){$(".owl-one").owlCarousel({loop:!0,margin:10,nav:!0,responsive:{0:{items:1},600:{items:1},1000:{items:3}}}),$(".owl-two").owlCarousel({loop:!0,margin:20,nav:!0,responsive:{0:{items:1},600:{items:1},1000:{items:2}}})});let bullets=Array.from(document.querySelectorAll(".bullets li"));bullets.forEach(e=>{e.addEventListener("click",e=>{bullets.forEach(e=>{e.classList.remove("active")}),e.currentTarget.classList.add("active"),document.querySelector(e.currentTarget.dataset.section).scrollIntoView({behavier:"smooth"})})});